import React, { useRef } from "react";
import styled from "styled-components";

const FormContainer = styled.form`
    display: flex;
    align-items: flex-end;
    gap: 10px;
    flex-wrap: wrap;
    bacground-color: #fff;
    padding: 20px;
    box-shadow: 0px 0px 5px #ccc;
    border-radius: 5px;

`;

const InputArea = styled.div`
    display: flex;
    flex-direction: column;

`;

const Label = styled.label``;

const Input = styled.input`
    width: 120px;
    padding: 0 10px;
    border: 1px solid #bbb;
    border-radius: 5px;
    height: 40px;

`;

const Form = ({ onEdit }) => {

    const ref = useRef();

    return (
        <FormContainer>

            <InputArea>
                <Label>Nome</Label>
                <Input name="nome" />
            </InputArea>

            <InputArea>
                <Label>E-mail</Label>
                <Input name="email" type="email" />
            </InputArea>

            <InputArea>
                <Label>Telefone</Label>
                <Input name="fone" />
            </InputArea>

            <InputArea>
                <Label>Telefone</Label>
                <Input name="fone" />
            </InputArea>

        </FormContainer>

    );

};

export default Form;
